mapper.update:
update cart
set qnt = qnt + #{qnt}
where pid=#{pid} and uid=#{uid}

mapper.select:
select count(*)
from cart
where pid=#{pid} and uid=#{uid}

        select * 
        from view_cart
        where uid=#{uid}
		order by regdate desc
		limit #{start}, #{size}

select count(*)
from cart
where uid=#{uid}
